/**
 * Objek global untuk mengelola fungsionalitas format.
 * Mengandung fungsi-fungsi helper untuk memformat data seperti tanggal dan angka.
 */
const FormatHelper = {
    /**
     * Memformat tanggal dari YYYY-MM-DD atau DD-MM-YYYY menjadi DD-MM-YYYY.
     *
     * @param {string} dateString String tanggal yang akan diformat.
     * @returns {string} Tanggal yang diformat atau string asli jika format tidak dikenali.
     */
    formatTanggal: function (dateString) {
        if (!dateString) return "";
        const dateParts = dateString.split("-");
        if (dateParts.length !== 3) return dateString;

        const [part1, part2, part3] = dateParts;
        const isYYYYMMDD =
            part1.length === 4 && part2.length === 2 && part3.length === 2;
        const isDDMMYYYY =
            part1.length === 2 && part2.length === 2 && part3.length === 4;

        if (isYYYYMMDD) {
            return `${part3}.${part2}.${part1}`;
        } else if (isDDMMYYYY) {
            return dateString;
        } else {
            return dateString;
        }
    },
};
